                tableContent.removeChild(children[i]);
                tableContent.appendChild(children[i]);
            }
        }

        function defaultIcon(element) {
            element.setAttribute(""uk-icon"", ""ratio: 0.7; icon: minus"");
            element.style.opacity = ""50%"";
        }
    </script>
");
